SWEP.Author = "Donkie"SWEP.Contact = "Through steam, thanks."SWEP.Purpose = "Attach parachutes to things."SWEP.Instructions = "LeftClick on object to attach a parachute to it.\nRightClick to parachute yourself.\nReload on parachute or object to remove its parachute."SWEP.Spawnable = trueSWEP.AdminSpawnable = trueSWEP.ViewModel = "models/weapons/v_pistol.mdl"SWEP.WorldModel = "models/weapons/w_pistol.mdl"if CLIENT then	SWEP.PrintName = "Parachuter"	SWEP.Slot = 2	SWEP.SlotPos = 2	SWEP.DrawAmmo = false	SWEP.DrawCrosshair = trueelse	AddCSLuaFile("shared.lua")	SWEP.Weight = 8	SWEP.AutoSwitchTo = true	SWEP.AutoSwitchFrom = falseendSWEP.FireDelay = 1SWEP.Primary.ClipSize = -1SWEP.Primary.DefaultClip = -1SWEP.Primary.Automatic = falseSWEP.Primary.Ammo = "none"SWEP.Secondary.ClipSize = -1SWEP.Secondary.DefaultClip = -1SWEP.Secondary.Automatic = falseSWEP.Secondary.Ammo = "none"function SWEP:Initialize()	self.delay = 0endfunction SWEP:CanFire()	if self.delay > CurTime() then return false end	self.delay = CurTime() + self.FireDelay	return trueendfunction SWEP:GetTraceEnt()	local ply = self.Owner		local td = util.TraceLine(util.GetPlayerTrace( ply ))		return td.Entity, td.HitPosendfunction SWEP:PrimaryAttack()	if not SERVER then return end	if not self:CanFire() then return end		local ent, pos = self:GetTraceEnt()	if not IsValid(ent) then return end		local loc = ent:WorldToLocal( pos )		parachute.DeployParachute_ent( self.Owner, ent, loc )endfunction SWEP:SecondaryAttack()	if not SERVER then return end	if not self:CanFire() then return end		self.Owner:ConCommand("para_act")endfunction SWEP:Reload()	if not SERVER then return end	if not self:CanFire() then return end		local ent = self:GetTraceEnt()	if not IsValid(ent) then return end		parachute.RemoveParachute_ent( self.Owner, ent )endfunction SWEP:Deploy()	return trueendSWEP.Holster = SWEP.Deploy